restSimpleBinding.url:
{let $uriParts := ($baseURL, "/reltio/api/", $tenantID, "/entities/", $entityId)

(: Optional Query Parameters :)
let $selectParam := if(empty($select)) then () else concat('select=', fn:encode-for-uri($select))
let $timeParam := if(empty($time)) then () else concat('$time=', fn:encode-for-uri($time))
let $optionsParam := if(empty($options) ) then () else concat('options=', fn:encode-for-uri($options))
let $mergeDuplicateCrosswalksParam := if(empty($mergeDuplicateCrosswalks)) then () else concat('mergeDuplicateCrosswalks=', fn:encode-for-uri($mergeDuplicateCrosswalks))
let $defaultMaxValuesParam := if(empty($defaultMaxValues)) then () else concat('defaultMaxValues=', fn:encode-for-uri($defaultMaxValues))

let $paramsArray:= ($selectParam, $timeParam, $optionsParam, $mergeDuplicateCrosswalksParam, $defaultMaxValuesParam)

let $queryParams:= if( empty($paramsArray) ) then () else string-join($paramsArray,"&amp;")

let $apiUrl := string-join($uriParts)
return if(empty($queryParams))
	then $apiUrl
    else concat($apiUrl, '?',$queryParams)}

{let $uriParts := ($baseURL ,"/reltio/api/", $tenantID, "/entities" )

(: Optional Query Parameters :)
let $filterParam := if(empty($filter)) then () else concat('filter=', fn:encode-for-uri($filter))
let $selectParam := if(empty($select)) then () else concat('select=', fn:encode-for-uri($select))
let $maxParam := if(empty($max)) then () else concat('max=', fn:encode-for-uri($max))
let $offsetParam := if(empty($offset)) then () else concat('offset=', fn:encode-for-uri($offset))
let $sortParam := if(empty($sort)) then () else concat('sort=', fn:encode-for-uri($sort))
let $orderParam := if(empty($order)) then () else concat('order=', fn:encode-for-uri($order))
let $optionsParam := if(empty($options) ) then () else concat('options=', fn:encode-for-uri($options))
let $defaultMaxValuesParam := if(empty($defaultMaxValues)) then () else concat('defaultMaxValues=', fn:encode-for-uri($defaultMaxValues))

let $paramsArray:= ($filterParam, $selectParam, $maxParam, $offsetParam, $sortParam, $orderParam, $optionsParam,$defaultMaxValuesParam)

let $queryParams:= if( empty($paramsArray) ) then () else string-join($paramsArray,"&amp;")

let $apiUrl := string-join($uriParts)
return if(empty($queryParams))
	then $apiUrl
    else concat($apiUrl, '?',$queryParams)}

