field.xquery:
{let $payload := $RESTResponse/*:payload/text()
let $entity_list := util:parseJSON($payload)
let $entityResp:=
for $item in $entity_list
return
<Entity>
 {$item/*}
</Entity>

return
<UpdateEntityResponse>
   <ReltioId>{ fn:substring-after($entityResp/object/uri, 'entities/') }</ReltioId>
   <Successful>{ if( exists($entityResp[1]/object/uri) ) then true() else false() }</Successful> 
</UpdateEntityResponse>}

{let $payload := $RESTResponse/*:payload/text()
let $entity_list := util:parseJSON($payload)
return 
for $item in $entity_list
return $item/errors}

restSimpleBinding.url:
{(: {{ExportServiceURL}}/export/{{Tenant}}/entities :)
let $uriParts := ($baseURL ,"/jobs/export/", $tenantID, "/entities" )

let $dateFormatParam := if (empty($dateFormat )) then () else concat("dateFormat=",encode-for-uri($dateFormat)) 
let $fileFormatParam  := if (empty($fileFormat)) then () else concat("fileFormat=",encode-for-uri($fileFormat )) 
let $distributedParam := if (empty($distributed)) then () else concat("distributed=",encode-for-uri($distributed  ))
let $taskPartsCountParam := if (empty($taskPartsCount)) then () else concat("taskPartsCount=",encode-for-uri($taskPartsCount))
let $useEsHeadersCollectionExportParam  := if (empty($useEsHeadersCollectionExport)) then () else concat("useEsHeadersCollectionExport=",encode-for-uri($useEsHeadersCollectionExport))
let $partSizeParam   := if (empty($partSize)) then () else concat("partSize=",encode-for-uri($partSize))
let $nameParam := if (empty($name)) then () else concat("name=",encode-for-uri($name))
let $selectParam := if (empty($select)) then () else concat("select=",encode-for-uri($select))
let $filterParam := if (empty($filter)) then () else concat("filter=",encode-for-uri($filter))
let $optionsParam := if (empty($options)) then () else concat("options=",encode-for-uri($options))
let $activenessParam := if (empty($activeness)) then () else concat("activeness=",encode-for-uri($activeness))
let $dataSourceParam  := if (empty($dataSource)) then () else concat("dataSource=",encode-for-uri($dataSource))
let $headerStyleParam := if (empty($headerStyle )) then () else concat("headerStyle=",encode-for-uri($headerStyle))
let $expoldedParam := if (empty($exploded )) then () else concat("exploded=",encode-for-uri($exploded))
(:param list construction:)
let $paramList := (
         $dateFormatParam 
        ,$fileFormatParam
        ,$distributedParam
        ,$taskPartsCountParam
        ,$useEsHeadersCollectionExportParam
        ,$partSizeParam
        ,$nameParam
        ,$selectParam
        ,$filterParam
        ,$optionsParam
        ,$activenessParam
        ,$dataSourceParam
        ,$headerStyleParam
        ,$expoldedParam)
(:complete url:)
let $queryParams:= if( empty($paramList) ) then () else string-join($paramList,"&amp;")

let $apiUrl := string-join($uriParts)
return if(empty($queryParams))
	then $apiUrl
    else concat($apiUrl, '?',$queryParams)}

restSimpleBinding.body:
{{
    {
    if (empty($exportFilter/ovOnly)) then () 
    else concat('"ovOnly": ', $exportFilter/ovOnly/text(),',')
    }
    {
    if (empty($exportFilter/headerFormat)) then () 
    else concat('"headerFormat": "', $exportFilter/headerFormat/text(),'",')
    }
    "updateTimeFilter": {{
        { for $element in  $exportFilter/updateTimeFilter/*
        return concat('"',local-name($element),'": ', $element/text())
        }
    }},
    "includeType": [
        {
        string-join(
        for $item in $exportFilter/includeType
        return concat('"',$item/text(),'"'),
        ",")
        }
    ]
}}
